import React from "react";
import { Component } from "react";

const stuff  = [
  {
  name: "Computing",
  rank: 15,
  salary: 12,
  desc: "This is the description of this major. It does stuff, stuff, stuff, stuff, stuff, stuff, stuff, stuff, yes, yes, and yes.",
  jobs: ["Job 1", "Job 2", "Job 3"]
  },
  {
  name: "Business",
  rank: 2,
  salary: 2020000,
  desc: "This is the description of this major. It does stuff, stuff, stuff, stuff, stuff, stuff, stuff, stuff, yes, yes, and yes.",
  jobs: ["Job 1", "Job 2", "Job 3"]
  },
  {
  name: "English",
  rank: 124,
  salary: 12034,
  desc: "This is the description of this major. It does stuff, stuff, stuff, stuff, stuff, stuff, stuff, stuff, yes, yes, and yes.",
  jobs: ["Job 1", "Job 2", "Job 3"]
  },
  {
  name: "Art",
  rank: 1,
  salary: 12,
  desc: "This is the description of this major. It does stuff, stuff, stuff, stuff, stuff, stuff, stuff, stuff, yes, yes, and yes.",
  jobs: ["Job 1", "Job 2", "Job 3"]
  },
  {
  name: "Science",
  rank: 10,
  salary: 12,
  desc: "This is the description of this major. It does stuff, stuff, stuff, stuff, stuff, stuff, stuff, stuff, yes, yes, and yes.",
  jobs: ["Job 1", "Job 2", "Job 3"]
  },
]

class Job extends Component {

  state = {
    majors: stuff,
    sort: ["NONE", 0],
  }

  expand = major => {
    console.log(major)
    let content = document.getElementById(major.name).nextElementSibling;
    if (content.style.display === "table-row") {
      content.style.display = "none";
      let temp = [...stuff];
      temp.push(major)
      console.log(stuff)
      this.setState({majors: temp})
    } else {
      content.style.display = "table-row";
    }
  }

  sort = type => {
    let temp = [...stuff];
    if(type == "RANK") {
      if(this.state.sort[0] != "RANK" || this.state.sort[1] == 0) {
        temp.sort(function(a, b){
          if(a.rank < b.rank) { return -1; }
          if(a.rank > b.rank) { return 1; }
          return 0;
        })
        this.setState({sort: ["RANK", 1]})
      } else if (this.state.sort[1] == 1) {
        temp.sort(function(a, b){
          if(a.rank > b.rank) { return -1; }
          if(a.rank < b.rank) { return 1; }
          return 0;
        })
        this.setState({sort: ["RANK", 2]});
      } else {
        this.setState({sort: ["RANK", 0]});
      }
    } else if (type == "SALARY") {
      if(this.state.sort[0] != "SALARY" || this.state.sort[1] == 0) {
        temp.sort(function(a, b){
          if(a.salary < b.salary) { return -1; }
          if(a.salary > b.salary) { return 1; }
          return 0;
        })
        this.setState({sort: ["SALARY", 1]})
      } else if (this.state.sort[1] == 1) {
        temp.sort(function(a, b){
          if(a.salary > b.salary) { return -1; }
          if(a.salary < b.salary) { return 1; }
          return 0;
        })
        this.setState({sort: ["SALARY", 2]});
      } else {
        this.setState({sort: ["SALARY", 0]});
      }
    }
    this.setState({majors:temp});
  }

render(){

let wrap = {
  width: "90vw",
  marginLeft: "auto",
  marginRight: "auto",
  height: "fit-content",
  borderCollapse: "collapse",
  borderColor: "#dcdcdc",

};

    return(
      <div>
      <div>
      <input style={{border:"1px solid #dcdcdc"}} type="text" placeholder="Search" onInput={(t) => console.log(t)}/>
      </div>


      <table border="1" frame="void" rules="rows" style={wrap}>
        <colgroup>
         <col style={{width: "3%", minWidth: "3%"}}/>
         <col style={{width: "67%", minWidth: "70%"}}/>
         <col style={{width: "10%", minWidth: "10%"}}/>
         <col style={{width: "20%", minWidth: "20%"}}/>
        </colgroup>
        <tr style={{background: "#f9f9f9", textAlign: "left", padding: "2vw", fontSize: "2.75vh"}}>
          <th></th>
          <th>Major</th>
          <th>Ranking <span onClick={() => this.sort("RANK")}>^</span></th>
          <th>Salary <span onClick={() => this.sort("SALARY")}>^</span></th>
        </tr>
        <tbody>
          {this.state.majors.map(major =>
          [
          <tr id={major.name} className="entry" onClick={() => this.expand(major)}>
           <td></td>
           <td style={{color:"#3db6ff", paddingTop:"0.5%", paddingBottom:"0.5%"}}>{major.name}</td>
           <td>{major.rank}</td>
           <td>${major.salary.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")}</td>
          </tr>,
          <tr className="entryInfo">
            <td></td>
            <td colspan="3" style={{paddingTop:"0.5%", paddingBottom:"0.5%"}}>
              <div style={{color: "black", fontWeight:"bold", fontSize:"1.5vh"}}>DESCRIPTION</div>
              <div>{major.desc}</div>
              <div style={{color: "black", fontWeight:"bold", fontSize:"1.5vh"}}>JOBS</div>
              <div>
              {major.jobs.map(job =>
                <div>{job}</div>
              )}
              </div>
              </td>
          </tr>
          ]
          )}
        </tbody>
      </table>
      </div>
    );
}

}

export default Job;
