import React from "react";
import { Component } from "react";
import axios from 'axios';

export default class Lunch extends Component {
  state = {
    lines: [],
  }

  async componentDidMount() {
    const res = await axios.get(`https://hse-schedule-api.herokuapp.com/api/lunch`)
    const data = res.data;
    this.setState({lines: data})
  }

  display = location => {
  }

  date = new Date();
  today = this.date.getDay() - 1;


  render() {

    let lineHeader = {
      margin: "0",
      background: "white",
      borderRadius: "10px 10px 0px 0px",
      padding: "5px 5px 0px 5px",
      border: "1px #d8d8d8 solid",
      width: "50vw"
    };

    let linePreview = {
      margin: "0",
      background: "white",
      borderRadius: "0px 10px 10px 0px",
      padding: "0px 5px 5px 5px",
      border: "1px #d8d8d8 solid",
      width: "50vw"
    };

    return (
      <div> {this.state.lines.map(lines =>
        <div className="lineButton" onClick={this.display(lines.Line)}>
          <h3 style={lineHeader}>{lines.Line}</h3>
          <div>
            <div style={linePreview} style={{whiteSpace: "pre-line"}}>{
              lines.Items[1][this.today - 1] ? (<div>{lines.Items[1][this.today - 1].replace(/\//gi, `\n`)}.</div>)
                : (<div>{lines.Items[1][this.today - 4].replace(/\//gi, `\n`)}</div>)
            }</div>
          </div>
          <div style={{display:"none"}}>
            <div className="lineItems">{lines.Line}</div>
          </div>
        </div>



        )}</div>
    )
  }
}
