import React from "react";
import { Component } from "react";


export default class Line extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: this.props.name,
      menu: this.props.menu,
      show: false,
      weeks: this.props.weeks,
      today: 1, /*this.props.today*/
    }
  }

  expand() {
    if(this.state.show) {
      this.setState({show: false})
    } else {
      this.setState({show: true})
    }
    console.log(this.state.show)
  }


  render() {

    let linePreview = {
      margin: "0",
      padding: "0px 5px 5px 5px",
      whiteSpace: "pre-line",
      color: "#464646",
    };

    let lineMenu = {
      margin: "0",
      padding: "0 5px 5px 5px",
      whiteSpace: "pre-line",
      color: "#464646",
    };


    return (
      <div style={{display: "flex", flexDirection: "row"}}>

        {(this.state.menu[1][0].length > 1 && this.state.name != "Daily") ?
          <div style={{marginTop:"auto", marginBottom:"auto", marginLeft:"auto"}}>
            <svg viewBox="64 64 896 896" xmlns="http://www.w3.org/2000/svg" focusable="false" data-icon="left" width="1em" height="1em" aria-hidden="true" style={{display: `${this.state.show ? ("inherit") : ("none")}`}}>
              <path d="M724 218.3V141c0-6.7-7.7-10.4-12.9-6.3L260.3 486.8a31.86 31.86 0 000 50.3l450.8 352.1c5.3 4.1 12.9.4 12.9-6.3v-77.3c0-4.9-2.3-9.6-6.1-12.6l-360-281 360-281.1c3.8-3 6.1-7.7 6.1-12.6z">
              </path>
            </svg>
          </div>
          : <div style={{marginLeft:"auto"}}></div>
        }



        <div className="hoverShadow lineButton" onClick={() => this.expand()}>
          <h3 className="lineHeader">{this.state.name}</h3>


          <div style={{display: `${this.state.show ? ("none") : ("inherit")}`}}>
          {this.state.name != "Daily" ?
            (<div style={linePreview}>{((this.state.today < 5 && this.state.today >= 0) &&
              this.state.menu[1][this.state.today][this.state.weeks.today]) ?
              <>{this.state.menu[1][2][0].replace(/\//gi, `\n`)}</>
              : (<></>)}</div>)
          :   (<div style={linePreview}>None</div>)

          }
          </div>
        </div>






        {(this.state.menu[1][0].length > 1 && this.state.name != "Daily") ?
          <div style={{marginTop:"auto", marginBottom:"auto", marginRight:"auto"}}>
            <svg viewBox="64 64 896 896" xmlns="http://www.w3.org/2000/svg" focusable="false" data-icon="left" width="1em" height="1em" aria-hidden="true" style={{display: `${this.state.show ? ("inherit") : ("none")}`}}>
              <path d="M765.7 486.8L314.9 134.7A7.97 7.97 0 00302 141v77.3c0 4.9 2.3 9.6 6.1 12.6l360 281.1-360 281.1c-3.9 3-6.1 7.7-6.1 12.6V883c0 6.7 7.7 10.4 12.9 6.3l450.8-352.1a31.96 31.96 0 000-50.4z">
              </path>
            </svg>
          </div>
          : <div style={{marginRight:"auto"}}></div>
        }
      </div>
    )
  }

}
