import React from "react";
import { Component } from "react";


export default class Menu extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: this.props.name,
      menu: this.props.menu,
      weeks: this.props.weeks,
      today: this.props.today,
      view: this.props.view,
    }

    this.days = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday"];
  }



  componentDidUpdate(previousProps) {
    if (this.props !== previousProps) {
      this.setState({
        view: this.props.view,
      })
    }
  }

  render() {


    return(

      <div style={this.props.style}>
      {this.days.map((day, i) =>
        <>
        <h4 style={{margin: "0", marginTop: "0.5vh", color: `${(i == this.state.today && this.state.view == this.state.week.today) ? ("#000") : ("#b1b1b1")}`, paddingLeft: "0.25vw"}}>{day.toUpperCase()}</h4>

        {this.state.menu[0].map((header, j) =>
          <>
          <h5 style={{margin: "0", paddingLeft: "0.75vw", fontSize: "0.63em", color: `${(i == this.state.today && this.state.view == this.state.week.today) ? ("#218dff") : ("#cfcfcf")}`}}>{header.toUpperCase()}</h5>
          <div style={{paddingLeft: "0.75vw", paddingBottom: "0.5vh", color: `${(i == this.state.today && this.state.view == this.state.week.today) ? ("#464646") : ("#ccc")}`}}>{this.state.menu[j + 1][i][this.state.view].replace(/\//gi, `\n`)}</div>
          </>

          )}
        </>

      )}
      </div>


    )

  }
}
