import React from "react";
import { Component } from "react";
import axios from 'axios';

export default class Lunch extends Component {
  state = {
    lines: [],
  }

  date = new Date();
  today = this.date.getDay();

  async componentDidMount() {
    const res = await axios.get(`https://hse-schedule-api.herokuapp.com/api/lunch`)
    const data = res.data;
    console.log(data)
    this.setState({lines: data})
    console.log("today: " + this.date);
  }

  display = location => {
    console.log(location)
    let menu = document.getElementById(location.toLowerCase() + "Menu");
    let prev = document.getElementById(location.toLowerCase() + "Preview")

    if(menu.style.display == "none") {
      prev.style.display = "none";
      menu.style.display = "";
    } else {
      prev.style.display = "";
      menu.style.display = "none";
    }
  }

  loadMenu = line => {
    let numberOfItems = line.Items[0].length;
    let days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday'];
    let innerHTML = ``;
      for(let i = 0; i < days.length; i++) {  // Loops through for the number of days
        innerHTML+= `<div><div>${days[i]}</div>`;
        for(let j = 0; j < numberOfItems; j++) {  // Loops through each item array
          console.log(line.Items)
          if(line.Items[j + 1][i][line.Week]) {
            innerHTML+= `<div>${line.Items[0][j]}</div>`;
            innerHTML+= `<div>${line.Items[j + 1][i][line.Week].replace(/\//gi, `\n`)}</div>`;
          }
        }
        innerHTML+= `</div>`;
      }
    return innerHTML;
  }

  render() {
    let lineButton = {
      borderRadius: "10px 10px 10px 10px",
      border: "1px #d8d8d8 solid",
      padding: "5px 0 0 0",
      background: "white",
      marginTop: "2vh",
      width: "50vw",
      marginLeft: "auto",
      marginRight: "auto",
      transition: "transform 0.3s ease",
      transform: "translateY(0px)",
    };

    let lineHeader = {
      margin: "0",
      padding: "0 5px 2px 5px",
      fontSize: "3vh"
    };

    let linePreview = {
      margin: "0",
      padding: "0px 5px 5px 5px",
      whiteSpace: "pre-line",
      color: "#464646",
    };

    let lineMenu = {
      margin: "0",
      padding: "0 5px 5px 5px",
      whiteSpace: "pre-line",
      color: "#464646",
    };

    let header = {
      borderBottom: "1px solid #bdbdbd",
      margin: "0",
      color: "#212121",
    }

    return (
      <div>
      <div style={{height: "10vh"}}></div>

       {this.state.lines.map(lines =>


        <div className="hoverShadow" style={lineButton} onClick={() => this.display(lines.Line)}>
          <h3 style={lineHeader}>{lines.Line}</h3>
          <div>
            <div id={(lines.Line.toLowerCase() + "Preview")} style={linePreview}>{
              lines.Items[1][this.today][lines.Week] ? (<div>{lines.Items[1][this.today][lines.Week].replace(/\//gi, `\n`)}</div>)
                : lines.Items[1][this.today] ? (<div>{lines.Items[1][this.today].replace(/\//gi, `\n`)}</div>)
                    : (<div>None</div>)

            }</div>
          </div>
          <div className="appear" id={(lines.Line.toLowerCase() + "Menu")} style={{display:"none"}}>
            <div style={lineMenu}>
              <h4 style={header}>Monday</h4>
                {this.loadMenu(lines)}
            </div>
          </div>
        </div>


        )}

        <div style={{height: "20vh"}}></div>
        </div>
    )
  }
}
