import React from "react";
import { Component } from "react";
import axios from 'axios';
import rightArrow from "./pictures/right.svg";
import leftArrow from "./pictures/left.svg";

export default class Lunch extends Component {
  state = {
    lines: [],
  }

  date = new Date();
  today = this.date.getDay() - 1;

  async componentDidMount() {
    const res = await axios.get(`https://hse-schedule-api.herokuapp.com/api/lunch`)
    const data = res.data;
    console.log(data)
    this.setState({lines: data})
    console.log("today: " + this.today);
  }

  display = location => {
    console.log(location)
    let menu = document.getElementById(location.toLowerCase() + "Menu");
    let prev = document.getElementById(location.toLowerCase() + "Preview");
    let l = document.getElementById(location.toLowerCase() + "Left");
    let r = document.getElementById(location.toLowerCase() + "Right");

    if(menu.style.display == "none") {
      prev.style.display = "none";
      menu.style.display = "";
      if(l) {
        l.style.display = "";
        r.style.display = "";
      }
    } else {
      prev.style.display = "";
      menu.style.display = "none";
      if(l) {
        l.style.display = "none";
        r.style.display = "none";
      }
    }
  }

  next = line => {
    let lineMenu = {
      margin: "0",
      padding: "0 5px 5px 5px",
      whiteSpace: "pre-line",
      color: "#464646",
    };

    let temp = [];
    Object.assign(temp, line)
    console.log(temp.Week)
    temp.Week = 1;
    let t = document.getElementById(temp.Line.toLowerCase() + "Menu");
    t = (<div style={lineMenu}>{this.loadMenu(temp)}</div>);
  }



  loadMenu = line => {

    let header = {
      margin: "0",
      marginTop: "0.5vh",
      color: "#b1b1b1",
      paddingLeft: "0.25vw",
    }

    let itemType = {
      margin: "0",
      paddingLeft: "0.75vw",
      fontSize: "0.63em",
      color: "#cfcfcf",
    }

    let item = {
      paddingLeft: "0.75vw",
      paddingBottom: "0.5vh",
      color: "#cccccc",
    }

    let todayMenu = {
      backgroundColor: "transparent",
      animationName: "pulse",
      animationDuration: "4s",
      animationIterationCount: "infinite",
    }

    let numberOfItems = line.Items[0].length;
    let days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday'];
    let menu = [];
    if(line.Line != "Daily") {
      for(let i = 0; i < days.length; i++) {  // Loops through for the number of days
        if(i == this.today) {
          menu.push(<h4 style={{...header, color: "black"}}>{days[i].toUpperCase()}</h4>);
        } else {
          menu.push(<h4 style={header}>{days[i].toUpperCase()}</h4>);
        }
        for(let j = 0; j < numberOfItems; j++) {  // Loops through each item array
          if(line.Items[j + 1][i][line.Week]) {
            if(i == this.today) {
              menu.push(<h5 style={{...itemType, color: "#218dff"}}>{line.Items[0][j].toUpperCase()}</h5>);
              menu.push(<div style={{...item, color: "#464646"}}>{line.Items[j + 1][i][line.Week].replace(/\//gi, `\n`)}</div>);
            } else {
              menu.push(<h5 style={itemType}>{line.Items[0][j].toUpperCase()}</h5>);
              menu.push(<div style={item}>{line.Items[j + 1][i][line.Week].replace(/\//gi, `\n`)}</div>);
            }
          }
        }
      }
    }
    return menu;
  }

  render() {
    let linePreview = {
      margin: "0",
      padding: "0px 5px 5px 5px",
      whiteSpace: "pre-line",
      color: "#464646",
    };

    let lineMenu = {
      margin: "0",
      padding: "0 5px 5px 5px",
      whiteSpace: "pre-line",
      color: "#464646",
    };

    return (
      <div>
      <div style={{height: "10vh"}}></div>

       {(this.state.lines != []) ? (this.state.lines.map(lines =>

        <div style={{display: "flex", flexDirection: "row"}}>
        {console.log(lines)}
        {(lines.Items[1][0].length > 1 && lines.Line != "Daily") ?
        <div style={{marginTop:"auto", marginBottom:"auto", marginLeft:"auto"}} onClick={() => this.next(lines)}>
        <svg id={(lines.Line.toLowerCase() + "Left")} viewBox="64 64 896 896" xmlns="http://www.w3.org/2000/svg" focusable="false" data-icon="left" width="1em" height="1em" aria-hidden="true" style={{display:"none"}}>
          <path d="M724 218.3V141c0-6.7-7.7-10.4-12.9-6.3L260.3 486.8a31.86 31.86 0 000 50.3l450.8 352.1c5.3 4.1 12.9.4 12.9-6.3v-77.3c0-4.9-2.3-9.6-6.1-12.6l-360-281 360-281.1c3.8-3 6.1-7.7 6.1-12.6z">
          </path>
        </svg>
        </div>
        : <div style={{marginLeft:"auto"}}></div>
        }
        <div className="hoverShadow lineButton" onClick={() => this.display(lines.Line)}>
          <h3 className="lineHeader">{lines.Line}</h3>
          <div>
          {console.log("Start step: " + lines.Line + "\n Week: " + lines.Week + "\n Day: " + this.today)}
            { (this.today < 5 && this.today >= 0) ? (<div id={(lines.Line.toLowerCase() + "Preview")} style={linePreview}>{
                lines.Items[1][this.today][lines.Week] ? (<div>{lines.Items[1][this.today][lines.Week].replace(/\//gi, `\n`)}</div>)
                  : lines.Items[1][this.today] ? (<div>{lines.Items[1][this.today].replace(/\//gi, `\n`)}</div>)
                      : (<div>None</div>)
                }</div>)
                : (<div id={(lines.Line.toLowerCase() + "Preview")} style={linePreview}>Weekend</div>)
            }
            {console.log("Step done: " + lines.Line)}
          </div>
          <div className="appear" id={(lines.Line.toLowerCase() + "Menu")} style={{display:"none"}}>
            <div style={lineMenu}>

                <div>{this.loadMenu(lines)}</div>
            </div>
          </div>
          {console.log("Complete: " + lines.Line)}
        </div>
          <div style={{marginTop:"auto", marginBottom:"auto", marginRight:"auto"}}>
          <svg id={(lines.Line.toLowerCase() + "Right")} viewBox="64 64 896 896" xmlns="http://www.w3.org/2000/svg" focusable="false" data-icon="left" width="1em" height="1em" aria-hidden="true" style={{display:"none"}}>
            <path d="M765.7 486.8L314.9 134.7A7.97 7.97 0 00302 141v77.3c0 4.9 2.3 9.6 6.1 12.6l360 281.1-360 281.1c-3.9 3-6.1 7.7-6.1 12.6V883c0 6.7 7.7 10.4 12.9 6.3l450.8-352.1a31.96 31.96 0 000-50.4z">
            </path>
          </svg>
          </div>
        </div>


          ))
          : (<div>loading...</div>)
        }

        <div style={{height: "20vh"}}></div>
        </div>
    )
  }
}
