import React from "react";
import { Component } from "react";


export default class Line extends Component {
  constructor(props) {
    super(props);
    this.state = {
      menu: this.props.day,
      show: false,
      items: this.props.items,
    }
  }

  expand() {
    if(this.state.show) {
      this.setState({show: false})
    } else {
      this.setState({show: true})
    }
