import React from "react";
import { Component } from "react";
import axios from 'axios';

export default class Lunch extends Component {
  state = {
    lines: [],
  }

  async componentDidMount() {
    const res = await axios.get(`https://hse-schedule-api.herokuapp.com/api/lunch`)
    const data = res.data;
    this.setState({lines: data})
  }

  display = location => {
  }

onChange = date => this.setState({ date })


  render() {
    let style = {
      width:"3vh",
      height:"3vh",
    };

    return (
      <div> {this.state.lines.map(lines =>
        <div className="lineButton" onClick={this.display(lines.Line)}>
          <div className="lineHeader">{lines.Line}</div>
          <div>
          {lines.Items.map(menu =>
            <div className="lineHeader">{menu}</div>
          )}
          </div>
          <div>
            <div className="lineItems">{lines.Line}</div>
          </div>
        </div>



        )}</div>
    )
  }
}
